import React, {useEffect, useState, useContext} from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import AppContext from '../AppContext';

import * as yup from 'yup';



function AddNewAccountForm({handleSubmit}) {
    const [holdSuggest, setHoldSuggest] = useState('')
    const [suggestions, setSuggestions] = useState([])
    const { loading, banks } = useContext(AppContext)
    const bank_names = banks.filter(bank=>{
        return bank.name.toLowerCase().includes(holdSuggest.toLowerCase())
    })

    const FormSchema = yup.object().shape({
        password: yup
            .string()
            .min(2, 'Password must be 2 characters long')
            .matches(/[a-z]/, 'Password requires a lowercase letter')
            .matches(/[A-Z]/, 'Password requires an uppercase letter')
            .matches(/[^\w]/, 'Password requires a symbol'),
        confirmPassword: yup
            .string()
            .oneOf([yup.ref('password'), null], 'Must match "password" field value'),
        username: yup
            .string()
            .min(5, 'username must be 5 characters long')
            .matches(/[a-z]/, 'username requires a lowercase letter')
            .matches(/[A-Z]/, 'username requires an uppercase letter'),
        confirmUsername: yup
            .string()
            .oneOf([yup.ref('username'), null], 'Must match "username" field value'),
        });

  return (
    <Formik
    initialValues={{ username: '', password: '', confirmPassword: '', confirmUsername: '' }}
    validationSchema={FormSchema}
    onSubmit={(values) => {
    handleSubmit(values.username, values.password);
    }}
>
    {({ isSubmitting, errors, handleChange }) => (

    <Form className='login_form_body'>

        {/* <div className='form_row'> */}
        <div className='form_fields'>
            <div className='form_row'>
                <label className="formik_labels">Bank Name: </label>
                <Field
                as="input"
                type="text" 
                name="confirmUsername" 
                className="formik_fields" 
                list="bankList"
                onChange={handleChange}/>

                <datalist id="bankList">
                {bank_names.map((item, key) =>
                  <option key={key} value={item.displayValue} />
                )}
                </datalist>
                {errors.username && <p>{errors.username}</p>}
            </div>
            <div className='form_row'>
                <label className="formik_labels">Account Value: </label>
                <Field type="password" name="password" className="formik_fields"/>
                {errors.password && <p>{errors.password}</p>}
            </div>
            {/* <div className='form_row'>
                <label className="formik_labels">Account Type: </label>
                <Field type="text" name="confirmUsername" className="formik_fields"/>
                {errors.confirmUsername && <p>{errors.confirmUsername}</p>}
            </div> */}
            {/* <div className='form_row'>
                <label className="formik_labels">Confirm P: </label>
                <Field type="password" name="confirmPassword" className="formik_fields"/>
                {errors.confirmPassword && <p>{errors.confirmPassword}</p>}
            </div> */}
          </div>


        <div className='form_row' style={{justifyContent:"center"}}>
        <button type="submit" disabled={isSubmitting} id='login_button'>
        Submit
        </button>

        </div>
    </Form>
    )}
</Formik>
  )
}

export default AddNewAccountForm
