import React, { createContext, useState, useEffect  } from 'react';
import { useLocation } from "react-router-dom";


const AppContext = createContext();


export const AppProvider = ({ children }) => {
  const [user, setUser] = useState(undefined);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [errors, setErrors] = useState([]);
  const [banks, setBanks] = useState([])
  const location = useLocation()

  useEffect(() => {
      fetch('/api/insights')
      .then((r) => r.json())
      .then(insightsData => {
      setCategories(insightsData);
    }).catch(error => {
      console.log("error in insights")
      setLoading(false);

    });
  }, [user]);
  useEffect(() => {
    fetch('/api/banks')
    .then((r) => r.json())
    .then(banks => {
      setBanks(banks);
  }).catch(error => {
    console.log("error in insights")
    setLoading(false);

  });
}, []);

  useEffect(() => {
    setLoading(true);
    fetch("/api/check_session")
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((user) => {
        setUser(user);
      })
      .catch((error) => {
        console.error("Error fetching session:", error);
        // Optionally set an error state here
      })
      .finally(() => {
        setLoading(false);
      });
  }, [location.pathname]);

  function handleLogin(username, password, setSubmitting, navigate) {

    fetch("/api/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ username, password }),
    })
      .then((r) => {
        if (r.ok) {
          r.json().then((user) => {
            setUser(user);
            navigate('/')
          });
        } else {
          r.json().then((err) => {
            setErrors([err.message || "Invalid login credentials. Please try again."]);
          });
        }
      })
      .catch((err) => {
        setErrors([err.message || "Network error. Please try again later."]);
      })
      .finally(()=>{
        setSubmitting(false)
      })

  }

  function mockLogin(username, password, navigate) {
    console.log("Nav test", navigate)
    fetch("/api/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ username, password }),
    })
      .then((r) => {
        if (r.ok) {
          r.json().then((user) => {
            setUser(user);
            navigate('/')
          });
        } else {
          r.json().then((err) => {
            setErrors([err.message || "Invalid login credentials. Please try again."]);
          });
        }
      })
      .catch((err) => {
        setErrors([err.message || "Network error. Please try again later."]);
      })
      // .finally(()=>{
      //   setSubmitting(false)
      // })

  }
  function handleTransactionSeed() {
    fetch('/api/transactionseed', {
        method: 'POST',  // Use POST for creating data
        headers: {
            'Content-Type': 'application/json',  // Send request as JSON
        },
    })
    .then(response => response.json())
    .then(document.location.reload())
    .catch(error => console.error('Error:', error));  // Handle any errors
}



  return (
    <AppContext.Provider value={{loading, banks, categories, user, setUser, setLoading, setCategories, handleLogin, errors, handleTransactionSeed, mockLogin }}>
      {children}
    </AppContext.Provider>
  );
};

export default AppContext;
