import React, {useState, useEffect, useContext} from 'react'
import AppContext from '../AppContext';
import '../Styling/App.css'
import { useNavigate } from 'react-router-dom';
import * as yup from 'yup';
import SignupForm from '../Components/SignupForm';

const FormSchema = yup.object().shape({
  password: yup
    .string()
    .min(2, 'Password must be 2 characters long')
    .matches(/[a-z]/, 'Password requires a lowercase letter')
    .matches(/[A-Z]/, 'Password requires an uppercase letter')
    .matches(/[^\w]/, 'Password requires a symbol'),
  confirmPassword: yup
    .string()
    .oneOf([yup.ref('password'), null], 'Must match "password" field value'),
  username: yup
    .string()
    .min(5, 'username must be 5 characters long')
    .matches(/[a-z]/, 'username requires a lowercase letter')
    .matches(/[A-Z]/, 'username requires an uppercase letter'),
  confirmUsername: yup
    .string()
    .oneOf([yup.ref('username'), null], 'Must match "username" field value'),
});


function Signup() {
    const navigate = useNavigate();
    const [errors, setErrors] = useState([]);
    const { setUser } = useContext(AppContext);

    const handleLoginGithub = () => {
      window.location.href = 'http://localhost:5555/api/login-github'; // Ensure the URL matches your Flask app's URL
    };
    useEffect(()=>{
      const queryString = window.location.search;
      const urlParams = new URLSearchParams(queryString);
      const codeParams = urlParams.get("code")
    },[handleLoginGithub])

    function handleSubmit(username, password) {
        fetch("/api/signup", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ username, password }),
        })
          .then((r) => {
            if (r.ok) {
              r.json().then((user) => {
                console.log(user);
                setUser(user);
                navigate('/');
              });
            } else {
              r.json().then((err) => {
                setErrors([err.message || "Invalid login credentials. Please try again."]);
              });
            }
          })
          .catch((err) => {
            setErrors([err.message || "Network error. Please try again later."]);
          })
      }


  return (
    <main>
        <div className='login_wrapper'>
            <div className='background_wrapper' id='login'>
            <div className='_wrapper' id='login'>
            <div className='login_content'>
                    <div className='login_header'><h2>Login</h2>
                    <p>Don't have an account? <a href='/signup' id = "signup">Signup</a>
                    </p>
                    </div>
                    <SignupForm 
                    handleSubmit={handleSubmit()}
                    FormSchema = {FormSchema}/>


            </div>
            </div>
            </div>
        </div>


    </main>
  )
}

export default Signup
