import React, { createContext, useState, useEffect } from 'react';

const AppContext = createContext();

export const AppProvider = ({ children }) => {
  const [banks, setBanks] = useState([]);
  const [accounts, setAccounts] = useState([]);
  const [user, setUser] = useState(undefined);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);



  function handleFetches(){
  useEffect(() => {
    setLoading(true);
    Promise.all([
      fetch('/api/banks').then((r) => r.json()),
      fetch('/api/account').then((r) => r.json()),
      fetch('/api/insights').then((r) => r.json())
    ]).then(([banksData, accountsData, insightsData]) => {
      setBanks(banksData);
      setAccounts(accountsData)
      setCategories(insightsData);
      setLoading(false);
    }).catch(error => {
      console.error('Error fetching data:', error);
      setLoading(false);
    });
  }, [setUser, setBanks, setAccounts])}
  handleFetches()



  return (
    <AppContext.Provider value={{loading, banks, categories, user, accounts, setUser, setBanks, setLoading }}>
      {children}
    </AppContext.Provider>
  );
};

export default AppContext;
