import React, {useContext, useState, useEffect} from 'react'
import BankComponentv2 from '../Components/BankComponentv2'
import Insights from '../Components/Insights'
import AppContext from '../AppContext'

function Home() {
    const { banks } = useContext(AppContext)
    const [isReady, setIsReady] = useState(false);

    useEffect(() => {
      const timer = setTimeout(() => {
        setIsReady(true); // Set to true after 1 second
      }, 1000);

      return () => clearTimeout(timer); // Cleanup if the component unmounts
    }, []);

    // Conditional rendering based on the isReady state
    if (!isReady) {
      return <div>Loading...</div>; // Display a loading indicator or spinner
    }
  return (
    <div className='home_wrapper'>
      <div className='background_wrapper'>   
          {/* <BankComponentv2 banks = {banks}/> */}
      </div>
          <div className='background_wrapper'>
          <div className='_wrapper' style={{padding:"50px"}}>  
            <h2 className='transaction_header'>Spending</h2>
            <Insights />
          </div>
        </div>
    </div>
  )
}

export default Home
