return (
  <div style={{ width: '300px', height: '300px', position: 'center' }}>
    {/* The PieChart itself */}
    <PieChart
    //     label={({ dataEntry }) => (hovered === dataEntry.title ? `${dataEntry.title}: ${dataEntry.value}` : dataEntry.value)}

      data={arrofObjects}
      onMouseOver={(_, index) => {
        setHoveredTitle(arrofObjects[index].title)
        setHoveredValue(arrofObjects[index].value)}}
      onMouseOut={() => {
        setHoveredTitle(null)
        setHoveredValue(null)}}
      lineWidth={30}
      paddingAngle={2}
      rounded
      segmentsStyle={{ transition: 'stroke .3s', cursor: 'pointer' }}
      segmentsShift={1}
    />

    {/* The center label */}
    <div
      style={{
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        textAlign: 'center',
        pointerEvents: 'none', // ensures the label doesn't block chart hover
      }}
    >
      <div style={{ fontWeight: 'bold', fontSize: '16px' }}>{hovered}</div>
      <div style={{ fontSize: '14px' }}>{hoveredValue}</div>
    </div>
  </div>
);
}

export default InsightsCharts
