import React, { useContext, useEffect, useState, createContext } from 'react'
import { useNavigate } from 'react-router-dom';

import './App.css';
import './Forms.css'
import './Modules.css'
import Nav from './Components/Nav';
import {Outlet } from 'react-router-dom';
import Login from './Pages/Login';
// import AppContext from './AppContext';


const AppContext = createContext();



function App() {
  const navigate=useNavigate()
  // const { user, loading, setUser } = useContext(AppContext);
   const [banks, setBanks] = useState([]);
    const [accounts, setAccounts] = useState([]);
    const [user, setUser] = useState(undefined);
    const [categories, setCategories] = useState([]);
    const [loading, setLoading] = useState(true);

      useEffect(() => {
        setLoading(true);
        Promise.all([
          fetch("/api/check_session").then((r) => r.json()),
          fetch('/api/banks').then((r) => r.json()),
          fetch('/api/account').then((r) => r.json()),
          fetch('/api/insights').then((r) => r.json())
        ]).then(([banksData, accountsData, insightsData]) => {
          if (response.ok) {
            response.json().then((user) => setUser(user));
          }
          setBanks(banksData);
          setAccounts(accountsData)
          setCategories(insightsData);
          setLoading(false);
        }).catch(error => {
          console.error('Error fetching data:', error);
          setLoading(false);
        });
      }, []);


  useEffect(() => {
    if (!loading && !user) {
      navigate('/login'); // Redirect only if not loading and user is null
    }
  }, [loading, user, navigate]);


  if (loading) {
    return <div>Loading...</div>; // Display loading indicator
  }
  if (!user) return <Login />
  if (!loading){
  return (
    <main>
      <AppContext.Provider value={{loading, banks, categories, user, accounts, setUser, setBanks, setLoading }}>
        <Nav />
        <Outlet />
      </AppContext.Provider>
    </main>
  );
}
}

export default App
