import React, {useState} from 'react'
import AppContext from '../AppContext'
import { useContext } from 'react'
import { PieChart } from 'react-minimal-pie-chart';



function InsightsCharts() {
  const [hovered, setHovered] = useState(null);
    const {categories} = useContext(AppContext)
    const colors = ['#E38627', '#C13C37', '#6A2135', '#8A2BE2', '#2E8B57']; 
    const defaultLabelStyle = {
      fontSize: '5px',
      fontFamily: 'sans-serif',
      color:"#fff",
    };
    const arrofObjects = Object.entries(categories).map(([key, value],index) => ({
      title: key,
      value: value,
      color: colors[index % colors.length]
  }));

  console.log('Chart Data:', arrofObjects);


      //   return <div style={{width:"100%"}}key={key} >

      //         <div className='transaction_rows'  style={{gridTemplateColumns: "3fr 2fr"}}>
      //         <p id='transaction_name'>{key}</p>
      //         <p id='Price'><span id='DollaBill'>$ </span>{value}</p>

      //         </div>
      // </div>


  return (
    <PieChart
    data={arrofObjects}
    // background={color}
    label={({ dataEntry }) => (hovered === dataEntry.title ? dataEntry.title : '')}
            onMouseOver={(_, index) => setHovered(arrofObjects[index].title)}
            onMouseOut={() => setHovered(null)}
            labelStyle={{ fontSize: '5px', fontWeight: 'bold', color: 'white' }}
            style={{ height: '300px' }}
    segmentsShift={(index) => (index === 5 ? 8 : 0.5)}
    labelPosition={80} // Adjusts label position

/>
  )
}

export default InsightsCharts
