import React, {useContext} from 'react'
import AppContext from '../AppContext';

function BanksComponentv2() {
  const { banks, loading } = useContext(AppContext)
  const formatter = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',

    // These options can be used to round to whole numbers.
    trailingZeroDisplay: 'stripIfInteger'
  });
  if (loading) {
    return <p>Loading...</p>;
  }

  useEffect(() => {
      // setLoading(true);
      Promise.all([
        fetch('/api/banks').then((r) => r.json()),
        fetch('/api/insights').then((r) => r.json())
      ]).then(([banksData, insightsData]) => {
        setBanks(banksData);
        setCategories(insightsData);
        // setLoading(false);
      }).catch(error => {
        console.error('Error fetching data:', error);
        // setLoading(false);
      });
    }, []);
    const bankElement = banks.length > 0? banks.map(bank=>{
        return(
            <div style={{width: "100%"}}>
              <h2 style={{padding:"15px 0", textAlign:"left"}}>{bank.bank_name}</h2>

                {bank.accounts.map(account=>{
                  return(
                  <div className='bank_account_container'>
                      <h4 ><a href={`account/${account.id}`}>{account.account_type}</a></h4>
                      <p>{formatter.format(account.account_value)}</p>
                  </div>)
                  }
                )
              }         
            </div>
        )
    }) : (
    <p>No banks available.</p>
  )
  return (
        <div className='_wrapper' id="login" style={{padding:"50px"}}>
          <h1>Accounts</h1>
          <div className='bank_id' style={{gap:"0px"}}>
            {bankElement}
          </div>
      </div>


  )
}

export default BanksComponentv2
