      setBanks(banks);
  }).catch(error => {
    console.log("Error in fetching banks", error)
    setLoading(false);

  });
}, []);

function handleNewAccountSubmission(bank_name,
  account_value,
  account_type,
  setSubmitting,
  navigate) {
  fetch(`/api/accounts`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    credentials: 'include',
    body: JSON.stringify({bank_name, account_value, account_type}),
  })
  .then((r) => r.json())
  .then(data=>{

    if (data) {
